import axios from 'axios';
import { editPostAction } from './postsReducer';

export const asyncEditPost = (obj, id) => {
	return (dispatch) => {
		axios
			.put('http://localhost:3001/posts/' + id, obj)
			.then(() => dispatch(editPostAction(obj)));
	};
};
