import React from 'react';
import moment from 'moment';
import 'moment/locale/ru';
import styles from './Homepage.module.scss';
import { useDispatch } from 'react-redux';
import { asyncAddNewPost } from '../../store/asyncAddNewPost';
import { useState } from 'react';

moment.locale('ru');
const defaultData = {
	date: '',
	equipment: '',
	category: '',
	reason: '',
	measures_taken: '',
	FIO: '',
	note: '',
};

const formFiels = [
	{ placeholder: 'Оборудование', tag: 'input', field: 'equipment' },
	{ placeholder: 'Вид неисправности', tag: 'input', field: 'category' },
	{ placeholder: 'Причина неисправности', tag: 'input', field: 'reason' },
	{ placeholder: 'Принятые меры', tag: 'input', field: 'measures_taken' },
	{ placeholder: 'ФИО, кто устранял', tag: 'input', field: 'FIO' },
	{ placeholder: 'Примечание', tag: 'input', field: 'note' },
];

const currentTime = moment().format('llll');

const Homepage = () => {
	const [data, setData] = useState(defaultData);
	const dispatch = useDispatch();
	const createPost = async (e) => {
		e.preventDefault();
		const title = prompt();
		// await axios.post('http://localhost:3001/posts', {
		// 	title,
		// });
		dispatch(asyncAddNewPost(title));
	};

	const changeInputs = (e) => {};

	return (
		<div className={styles.home}>
			<div className={styles.formWrapper}>
				<h1>Создание записи</h1>
				<form onSubmit={createPost} className={styles.form}>
					<label>
						<span>Дата заявки</span>
						<input type='text' disabled />
					</label>
					<label>
						<span>Оборудование</span>
						<textarea value={data.equipment} />
					</label>
					<label>
						<span>Вид неисправности</span>
						<select>
							<option value='поломка'>поломка</option>
							<option value='прогар'>прогар</option>
							<option value='ошибка сети'>ошибка сети</option>
						</select>
					</label>
					<label>
						<span>Причина неисправности</span>
						<input type='text' />
					</label>
					<label>
						<span> Принятые меры</span>
						<textarea />
					</label>
					<label>
						<span>ФИО, кто устранял</span>
						<input type='text' />
					</label>
					<label>
						<span> Примечание</span>
						<input type='text' />
					</label>
					<button>Создать запись</button>
				</form>
			</div>
		</div>
	);
};

export default Homepage;
