import axios from 'axios';
import { getAllPostsAction } from './postsReducer';

export const fetchPosts = () => {
	return (dispatch) => {
		axios
			.get('http://localhost:3001/posts')
			.then((data) => dispatch(getAllPostsAction(data.data)));
	};
};
