import axios from 'axios';
import React, { useState } from 'react';

const defaultData = {
	surname: '',
	name: '',
	time_create: 0,
};

const Testpage = () => {
	const [data, setData] = useState(defaultData);
	const [deletable, setDeletable] = useState('');

	const sendData = (e) => {
		e.preventDefault();
		const person = { ...data, time_create: Date.now() };
		axios
			.post('http://localhost:8080/api/posts/', person)
			.then(() => setData(defaultData));
	};

	const getData = () => {
		axios.get('http://localhost:8080/api/posts/').then((res) => console.log(res.data));
	};

	const deletePost = () => {
		axios
			.delete('http://localhost:8080/api/posts/' + deletable)
			.then(() => setDeletable(''));
	};

	return (
		<form onSubmit={sendData}>
			<input
				onChange={(e) => setData((prev) => ({ ...prev, name: e.target.value }))}
				value={data.name}
				type='text'
				placeholder='name'
			/>
			<input
				onChange={(e) => setData((prev) => ({ ...prev, surname: e.target.value }))}
				value={data.surname}
				type='text'
				placeholder='surname'
			/>
			<button>send</button>
			<div>
				<br />
				<br />
				<button type='button' onClick={getData}>
					Получить посты
				</button>
				<br />
				<br />
				<input
					min={0}
					type='number'
					value={deletable}
					onChange={(e) => setDeletable(e.target.value)}
				/>
				<div>
					<button type='button' onClick={deletePost}>
						Удалить пост
					</button>
				</div>
			</div>
		</form>
	);
};

export default Testpage;
