import axios from 'axios';
import React, { useState, useEffect } from 'react';
import styles from './Singlepostpage.module.scss';
import { FaArrowAltCircleLeft } from 'react-icons/fa';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
const Singlepostpage = () => {
	const navigator = useNavigate();
	const { id: currentId } = useParams();
	const [currentPost, setCurrentPost] = useState(null);

	useEffect(() => {
		setTimeout(() => {
			axios
				.get('  http://localhost:3001/posts/' + currentId)
				.then((data) => setCurrentPost(data.data));
		}, 700);
	}, [currentId]);

	if (currentPost !== null) {
		return (
			<>
				<button className={styles.goback} onClick={() => navigator(-1)}>
					<FaArrowAltCircleLeft />
					<span>Назад</span>
				</button>
				<div className={styles.post}>
					<h2>Причина поломки: {currentPost.reason}</h2>
				</div>
			</>
		);
	} else {
		return <h1>Загрузка...</h1>;
	}
};

export default Singlepostpage;
