import axios from 'axios';
import { deletePostAction } from './postsReducer';

export const deletePost = (id) => {
	return (dispatch) => {
		axios
			.delete('http://localhost:3001/posts/' + id)
			.then(() => dispatch(deletePostAction(id)));
	};
};
