const FETCH_ALL_POSTS = 'FETCH_ALL_POSTS';
const ADD_NEW_POST = 'ADD_NEW_POST';
const EDIT_POST = 'EDIT_POST';
const DELETE_POST = 'DELETE_POST';

export const postsReducer = (state = [], action) => {
	switch (action.type) {
		case FETCH_ALL_POSTS:
			return action.payload;

		case ADD_NEW_POST:
			return [...state, action.payload];

		case DELETE_POST:
			return state.filter((post) => post.id !== action.payload);

		case EDIT_POST:
			const copy = [...state];
			const current = state.find((p) => p.id === action.payload.id);
			// console.log(current);
			return copy;
		default:
			return state;
	}
};

export const getAllPostsAction = (payload) => ({ type: FETCH_ALL_POSTS, payload });
export const addNewAction = (payload) => ({ type: ADD_NEW_POST, payload });
export const deletePostAction = (payload) => ({ type: DELETE_POST, payload });
export const editPostAction = (payload) => ({ type: EDIT_POST, payload });
