import axios from 'axios';
import { addNewAction } from './postsReducer';

export const asyncAddNewPost = (post) => {
	return (dispatch) => {
		axios
			.post('http://localhost:3001/posts', post)
			.then((data) => dispatch(addNewAction(data.data)));
	};
};
