import React from 'react';
import moment from 'moment';
import 'moment/locale/ru';
import styles from './Homepage.module.scss';
import { useDispatch } from 'react-redux';
import { asyncAddNewPost } from '../../store/asyncAddNewPost';
import { useState } from 'react';

moment.locale('ru');
const defaultData = {
	date: '',
	equipment: '',
	category: '',
	reason: '',
	measures_taken: '',
	FIO: '',
	note: '',
};

const formFields = [
	{ placeholder: 'Оборудование', tag: 'input', field: 'equipment' },
	{ placeholder: 'Вид неисправности', tag: 'input', field: 'category' },
	{ placeholder: 'Причина неисправности', tag: 'input', field: 'reason' },
	{ placeholder: 'Принятые меры', tag: 'input', field: 'measures_taken' },
	{ placeholder: 'ФИО, кто устранял', tag: 'input', field: 'FIO' },
	{ placeholder: 'Примечание', tag: 'input', field: 'note' },
];

const currentTime = moment().format('llll');

const Homepage = () => {
	const [data, setData] = useState(defaultData);
	const dispatch = useDispatch();
	const createPost = async (e) => {
		e.preventDefault();
		const title = prompt();
		// await axios.post('http://localhost:3001/posts', {
		// 	title,
		// });
		dispatch(asyncAddNewPost(title));
	};

	const changeInputs = (e) => {};

	return (
		<div className={styles.home}>
			<div className={styles.formWrapper}>
				<h1>Создание записи</h1>
				<form onSubmit={createPost} className={styles.form}>
					{formFields.map((f) => (
						<label>
							<span>{f.placeholder}</span>
							{f.tag === 'input' ? <input type='text' /> : <textarea />}
						</label>
					))}
					<button>Создать запись</button>
				</form>
			</div>
		</div>
	);
};

export default Homepage;

