import React from 'react';
import styles from './Homepage.module.scss';
import { useDispatch } from 'react-redux';
import { asyncAddNewPost } from '../../store/asyncAddNewPost';
import { useState } from 'react';

const defaultData = {
	title: '',
	date: '',
	equipment: '',
	category: '',
	reason: '',
	measures_taken: '',
	FIO: '',
	note: '',
};

const Homepage = () => {
	const [data, setData] = useState();
	const dispatch = useDispatch();
	const createPost = async (e) => {
		e.preventDefault();
		const title = prompt();
		// await axios.post('http://localhost:3001/posts', {
		// 	title,
		// });
		dispatch(asyncAddNewPost(title));
	};

	return (
		<div className={styles.home}>
			<div className={styles.formWrapper}>
				<h1>Создание записи</h1>
				<form onSubmit={createPost} className={styles.form}>
					<label>
						<span>Дата заявки</span>
						<input type='text' disabled />
					</label>
					<label>
						<span>Оборудование</span>
						<textarea />
					</label>
					<label>
						<span>Вид неисправности</span>
						<select>
							<option value='поломка'>поломка</option>
							<option value='прогар'>прогар</option>
							<option value='ошибка сети'>ошибка сети</option>
						</select>
					</label>
					<label>
						<span>Причина неисправности</span>
						<input type='text' />
					</label>
					<label>
						<span> Принятые меры</span>
						<textarea />
					</label>
					<label>
						<span>ФИО, кто устранял</span>
						<input type='text' />
					</label>
					<label>
						<span> Примечание</span>
						<input type='text' />
					</label>
					<button>Создать запись</button>
				</form>
			</div>
		</div>
	);
};

export default Homepage;
